import React from 'react';

import {
    Card, CardImg, CardText, CardBody,
    CardTitle, Breadcrumb, BreadcrumbItem
} from 'reactstrap';
import { Link } from 'react-router-dom';


function RenderDish({ dish }) {

    return (
        <div className="col-12 col-md-5 m-1">

            <Card>
                <CardImg top src={dish.image} alt={dish.name} />
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                </CardBody>

            </Card>

        </div>
    );

}

function RenderComments({ comments }) {

    if (comments)
        return (
            <div className="col-12 col-md-5 m-1">

                <h4>Comments</h4>
                {comments.map((item, i) => <ul className='list-unstyled' ><li  >{item.comment}</li><li className='list-unstyled'>--{item.author}, {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit' }).format(new Date(Date.parse(item.date)))}</li></ul>)}
            </div>
        );
    else
        return (
            <div></div>
        );

}

const DishdetailComponent = (props) => {
    // console.log(props.dish[0]);
    if (props.dish != null)
        return (
            <div className="container">
                <div className="row">
                    <Breadcrumb>
                        {/* <BreadcrumbItem>
                                <Link to="/home">Home</Link>
                            </BreadcrumbItem> */}
                        <BreadcrumbItem>
                            <Link to="/menu">Menu</Link>
                        </BreadcrumbItem>
                        <BreadcrumbItem active>
                            {props.dish.name}
                        </BreadcrumbItem>
                    </Breadcrumb>
                    <div className="col-12">
                        <h3>{props.dish.name}</h3>
                        <hr />
                    </div>
                </div>
                <div className="row">
                    <div className="col-12 col-md-5 m-1">
                        <RenderDish dish={props.dish} />
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        <RenderComments comments={props.comments} />
                    </div>
                </div>

                {/* <RenderDish dish={props.dish} />
                    <RenderComments comments={props.dish.comments} />                   */}

            </div>
        );
    else
        return (
            <div></div>

        );

}



export default DishDetail;
