import React, { Component } from 'react';
import { Card, CardImage, CardImageOverlay, CardText, CardBody, CardTitle } from 'reactstrap';

class Menu extends Component {

    render() {
        const menu = this.props.dishes.map((dish) => {
            return (
                <div key={dish.id} className="col-12 cold-md-5 mt-1">
                    <Card tag="li">
                        <CardImage width="100%" src={dish.image} alt={dish.name} />
                        <CardImageOverlay body className="ml-5">
                            <CardTitle heading>{dish.name}</CardTitle>
                            {/* <p> {dish.description}></p> */}
                        </CardImageOverlay>
                    </Card>
                </div>
            );
        });
        return (
            <div className="container">
                <div className="row">
                    <Media list>
                        {menu}
                    </Media>
                </div>
            </div>
        );
    }
}

export default Menu;
